import React from 'react';
import './index.css';
import API from '../../utils/API';

export default class TestScreen extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            questions: [],
            currentIndex: 0,
            isLoading: true
        }
    }

    componentDidMount = async () => {
        API.get('mockTest/')
            .then(res => {
                this.setState({ questions: res.data, isLoading: false });
            })

    }

    renderOptions = () => {
        const { currentIndex, questions } = this.state;
        const { options } = questions[currentIndex];
        return (
            <div className="optionsContainer">
                {options.map(option => {
                    const { isImage, imageUrl } = option;
                    return (
                        <div className="optionsRow">
                            <div className="optionNumberContainer">
                                {option.optionNumber}
                            </div>
                            {isImage
                                ?
                                <div className="optionImageContainer">
                                    <img
                                        src={imageUrl}
                                        className="optionImage" />
                                </div>
                                :
                                <div className="optionTextContainer">
                                    {text}
                                </div>}
                        </div>
                    )
                })}
            </div>
        )
    }

    renderQuestion = () => {
        const { currentIndex, questions } = this.state;
        const { questionNumber, text, isImage, imageUrl } = questions[currentIndex];
        return (
            <div className="questionContainer">
                <div className="questionHeader">
                    <div className="questionNumberContainer">
                        {questionNumber}
                    </div>
                    {isImage
                        ?
                        <div className="questionImageContainer">
                            <img
                                src={imageUrl}
                                className="questionImage" />
                        </div>
                        :
                        <div className="questionTextContainer">
                            {text}
                        </div>}
                </div>
                {this.renderOptions()}
            </div>
        )
    }

    render() {
        const { isLoading } = this.state;
        return (
            <div className="container">
                {isLoading
                    ?
                    <div>Loading</div>
                    :
                    this.renderQuestion()}
            </div>
        )
    }
}
