import React from 'react';
import './index.css';
import API from '../../utils/API';

export default class TestScreen extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            questions: [],
            currentIndex: 0,
            isLoading: true
        }
    }

    componentDidMount = async () => {
        API.get('mockTest/')
            .then(res => {
                this.setState({ questions: res.data, isLoading: false });
            })

    }

    renderQuestion = () => {
        const { currentIndex, questions } = this.state;
        return (
            <div className="questionContainer">
                <div className="questionHeader">
                    <div className="questionNumberContainer">
                        {questions[currentIndex].questionNumber}
                    </div>
                    <div className="questionTextContainer">
                        {questions[currentIndex].text}
                    </div>
                </div>
            </div>
        )
    }

    render() {
        const { isLoading } = this.state;
        return (
            <div className="container">
                <div>
                    This is the Test Screeen
                </div>

                {isLoading ?
                    <div>Loading</div> :
                    this.renderQuestion()}
            </div>
        )
    }
}
